const mongoose = require("mongoose");

// Define the schema for the note
const noteSchema = new mongoose.Schema({
  title: {
    type: String,
    required: true,
  },
  content: {
    type: String,
    required: true,
  },
  createdAt: {
    type: Date,
    default: Date.now,
  },
  updatedAt: {
    type: Date,
    default: Date.now,
  },
});

// Create a model based on the schema
const Note = mongoose.model("Note", noteSchema);

exports.Note = Note;
